  constructor(private servicioFavorito: ServicioFavoritosService) { }


  ngOnInit(): void {
    this.ArregloTarjetas = [
      {titulo:'GELATINA DE FRUTAS', Descripcion: 'Descripcion: Gelatina sabor pi침a con trozos de duraznos, cerezas y uvas', image: "./assets/Frut1.jpg"
