      {titulo:'GELATINA UVA Y QUESO CREMA', Descripcion: 'Descripcion: Gelatina sabor limon con uvas y queso crema', image:"./assets/Frutal3.jpg"
      },
      {titulo:'GELATINA GIRASOL', Descripcion: 'Descripcion; Gelatina sabor durazno y queso crema, con duraznos y moras azules', image:"./assets/Frutal4.jpg"
      },

     ]
     }

  AgregarAFavorito()
  {
